    /**
     * 富文本内容到word（本地）
     * @param content 输出内容
     * @param fileName 导出文件名称
     * @throws Exception
     */
    public static void exportHtmlToWord(String filepath, String content) throws Exception {
        // 拼接html格式内容
        StringBuffer sbf = new StringBuffer();
        // 这里拼接一下html标签,便于word文档能够识别
        sbf.append("<html " +
                "xmlns:v=\"urn:schemas-microsoft-com:vml\" xmlns:o=\"urn:schemas-microsoft-com:office:office\" xmlns:w=\"urn:schemas-microsoft-com:office:word\" xmlns:m=\"http://schemas.microsoft.com/office/2004/12/omml\" xmlns=\"http://www.w3.org/TR/REC-html40\"" + //将版式从web版式改成页面试图
                ">");
        sbf.append("<head>" +
                "<!--[if gte mso 9]><xml><w:WordDocument><w:View>Print</w:View><w:TrackMoves>false</w:TrackMoves><w:TrackFormatting/><w:ValidateAgainstSchemas/><w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid><w:IgnoreMixedContent>false</w:IgnoreMixedContent><w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText><w:DoNotPromoteQF/><w:LidThemeOther>EN-US</w:LidThemeOther><w:LidThemeAsian>ZH-CN</w:LidThemeAsian><w:LidThemeComplexScript>X-NONE</w:LidThemeComplexScript><w:Compatibility><w:BreakWrappedTables/><w:SnapToGridInCell/><w:WrapTextWithPunct/><w:UseAsianBreakRules/><w:DontGrowAutofit/><w:SplitPgBreakAndParaMark/><w:DontVertAlignCellWithSp/><w:DontBreakConstrainedForcedTables/><w:DontVertAlignInTxbx/><w:Word11KerningPairs/><w:CachedColBalance/><w:UseFELayout/></w:Compatibility><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><m:mathPr><m:mathFont m:val=\"Cambria Math\"/><m:brkBin m:val=\"before\"/><m:brkBinSub m:val=\"--\"/><m:smallFrac m:val=\"off\"/><m:dispDef/><m:lMargin m:val=\"0\"/> <m:rMargin m:val=\"0\"/><m:defJc m:val=\"centerGroup\"/><m:wrapIndent m:val=\"1440\"/><m:intLim m:val=\"subSup\"/><m:naryLim m:val=\"undOvr\"/></m:mathPr></w:WordDocument></xml><![endif]-->" +
                "</head>");
        sbf.append("<body>");
        // 富文本内容
        sbf.append(content);
        sbf.append("</body></html>");

        // 必须要设置编码,避免中文就会乱码
        byte[] b = sbf.toString().getBytes("GBK");
        // 将字节数组包装到流中
        ByteArrayInputStream bais = new ByteArrayInputStream(b);
        POIFSFileSystem poifs = new POIFSFileSystem();
        DirectoryEntry directory = poifs.getRoot();
        // 这代码不能省略,否则导出乱码。
        DocumentEntry documentEntry = directory.createDocument("WordDocument", bais);

        FileOutputStream out = new FileOutputStream(new File(filepath));
        poifs.writeFilesystem(out);
        bais.close();
        out.close();
    }



    /**
     * 通过poi生成word文件
     * @param html html富文本
     * @param path 保存路径
     */
    public static void htmlToWord(String html,String path) {
        POIFSFileSystem poifs = null;
        FileOutputStream ostream = null;
        ByteArrayInputStream bais = null;
        File file = null;
        try {
            //HTML内容必须被<html><body></body></html>包装
            byte[] b = wrappHtml(html).getBytes();
            bais = new ByteArrayInputStream(b);
            poifs = new POIFSFileSystem();
            DirectoryEntry directory = poifs.getRoot();
            //WordDocument名称不允许修改
            directory.createDocument("WordDocument", bais);
            ostream = new FileOutputStream(path);
            poifs.writeFilesystem(ostream);//当前目录下就生成了一个测试.doc的文档
            ostream.flush();
        } catch (Exception e) {
            e.printStackTrace();
        } finally {

            IOUtils.closeQuietly(ostream);
            IOUtils.closeQuietly(bais);
        }
    }

    /**
     * 拼接html字符串,加上<html><body></body></html>
     * @param html html
     * @return 拼接完成后的html字符串
     */
    private static String wrappHtml(String html){
        // 因为传递过来都是不完整的doc
        StringBuilder sb = new StringBuilder();
        sb.append("<html>");
        sb.append("<body>");
        sb.append(html);

        sb.append("</body>");
        sb.append("</html>");
        return sb.toString();
    }

    /**
     * 富文本转换成html，通过pandoc生成doc
     * 此方法需要本地安装pandoc
     * @param html 富文本字符串
     * @param path 保存路径
     *  @param cmd pandoc命令
     * @throws Exception
     */
    public static void htmlToWord(String html, String path,String [] cmd) throws Exception{
        FileOutputStream fos = new FileOutputStream(path);
        fos.write(html.getBytes("utf-8"));
        fos.close();
        Process exec = Runtime.getRuntime().exec(cmd);
        InputStreamReader inputStreamReader = new InputStreamReader(exec.getInputStream());
        BufferedReader bufferedReader = new BufferedReader(inputStreamReader);
        String line = null;
        while ((line = bufferedReader.readLine()) != null ) {
            System.out.println("输入流" + line);
        }
        InputStreamReader inputStreamReader1 = new InputStreamReader( exec.getErrorStream());
        BufferedReader bufferedReader1 = new BufferedReader(inputStreamReader1);
        while ((line = bufferedReader1.readLine()) != null ) {
            System.out.println("输error流" + line);
        }
        int result = exec.waitFor();
        System.out.println("执行结果" + result);
        File file = new File(path);
        if(file.exists()){
            file.delete();
        }
    }


